\begin{tikzpicture}[x=1.8cm, y=1.2cm,
    node/.style={circle, draw, minimum size=6mm, inner sep=0pt, fill=blue!15},
    >=latex]
  \foreach \i in {1,...,3}
    \node[node] (I-\i) at (0,-\i) {};
  \foreach \j in {1,...,4}
    \node[node, fill=orange!20] (H-\j) at (1.5,-\j+0.5) {};
  \foreach \k in {1,...,2}
    \node[node, fill=green!20] (O-\k) at (3,-\k-0.5) {};
  \foreach \i in {1,...,3}
    \foreach \j in {1,...,4}
      \draw[->] (I-\i) -- (H-\j);
  \foreach \j in {1,...,4}
    \foreach \k in {1,...,2}
      \draw[->] (H-\j) -- (O-\k);
  \node[above] at (0,-0.5) {\small Input};
  \node[above] at (1.5,0) {\small Hidden};
  \node[above] at (3,-0.5) {\small Output};
\end{tikzpicture}

